!function(t) {
    "use strict";
    
   // Sidebar toggle function
  t("#sidebarToggle, #sidebarToggleTop").on("click", function(o) { 
  t("body").toggleClass("sidebar-toggled");
  t(".sidebar").toggleClass("toggled");

  // Collapse the sidebar if toggled
  if (t(".sidebar").hasClass("toggled")) {
    // Change icon to "X"
    t("#menuIcon").removeClass("fa-bars").addClass("fa-times");
    
    // Collapse all open dropdowns
    t(".sidebar .collapse").collapse("hide");
  } else {
    // Change icon back to hamburger
    t("#menuIcon").removeClass("fa-times").addClass("fa-bars");
    
    // Optionally, you can expand a specific dropdown if needed
    // t(".sidebar .collapse").collapse("show"); // Uncomment if you want to show a specific dropdown
  }
});

    // Auto collapse sidebar on window resize if less than 768px
    t(window).resize(function() {
      if (t(window).width() < 768) {
        t(".sidebar .collapse").collapse("hide");
      }
    });
  
    // Prevent sidebar from scrolling if fixed-nav is enabled
    t("body.fixed-nav .sidebar").on("mousewheel DOMMouseScroll wheel", function(o) {
      if (t(window).width() > 768) {
        var e = o.originalEvent,
            l = e.wheelDelta || -e.detail;
        this.scrollTop += 30 * (l < 0 ? 1 : -1);
        o.preventDefault();
      }
    });
  
    // Show/hide scroll-to-top button based on scroll position
    t(document).on("scroll", function() {
      t(this).scrollTop() > 100 ? t(".scroll-to-top").fadeIn() : t(".scroll-to-top").fadeOut();
    });
  
    // Smooth scrolling to top when the scroll-to-top button is clicked
    t(document).on("click", "a.scroll-to-top", function(o) {
      var e = t(this);
      t("html, body").stop().animate({
        scrollTop: t(e.attr("href")).offset().top
      }, 1000, "easeInOutExpo");
      o.preventDefault();
    });
  
  }(jQuery);
  